import { Topbar } from "../component/Topbar";
import Cards from "../component/Cards";
import { useState, useEffect, useCallback } from "react";
import CardDetail from "../component/CardDetail";
import { Footer } from "../component/Footer";

export function Home() {
  const [data, setData] = useState(null);
  const [openDetails, setOpenDetails] = useState(false);
  const [singleCard, setSingleCard] = useState();

  const handleDetail = (singleCard) => {
    setOpenDetails(true);
    setSingleCard(singleCard);
  };
  const closeDetail = () => {
    setOpenDetails(false);
  };

  useEffect(() => {
    fetch(`https://zoo-animal-api.herokuapp.com/animals/rand/${8}`)
      .then((response) => response.json())
      .then((json) => {
        setData(json);
      });
  }, []);
  const setBg = openDetails ? "bg-change" : "";
  console.log(data);

  return (
    <div className="bg-[#3c3c3c]">
      <Topbar />
      {data && (
        <Cards
          singleCard={singleCard}
          handleDetail={handleDetail}
          data={data}
        />
      )}
      <div id="bg-cards" className={setBg}></div>
      {openDetails && (
        <CardDetail singleCard={singleCard} closeDetail={closeDetail} />
      )}
      <Footer />
    </div>
  );
}
